import React          from 'react';
import { Component }  from 'react';
import { TableHead }  from './TableHead';
import TableBody      from './TableBody';



export default class Table extends Component{

	constructor(props){
		 super(props);

		 this.state = {
			  currentCoins : props.input
		 }
	 }

 static getDerivedStateFromProps( nextProps, prevState ) {
            if (nextProps.input !== prevState.input) {

                  return { currentCoins: nextProps.input };
            }
            else return null;
       }

	render(){

          const { currentCoins } = this.state;

		return (
      <table>
        <div>
          <tr>
            <th id="logo_title" className="headTopBorder headLeftBorder"></th>

            <th id="symbol_title" className="centerText headTopBorder">
              Symbol
            </th>

            <th id="name_title" className="centerText headTopBorder">
              Name
            </th>

            <th id="invisible_title" className="headTopBorder">
              Symbol
              <br />
              Name
            </th>

            <th className="headTopBorder centerText">Price USD</th>

            <th className="headTopBorder">Change 24h</th>

            <th id="chart_title" className="headTopBorder centerText">
              24h Chart
            </th>

            <th id="invisible_two_title" className="headTopBorder">
              Volume 24h
              <br />
              Market Cap
            </th>

            <th id="volume_24h_title" className="headTopBorder">
              Volume 24h
            </th>

            <th id="market_cap_title" className="headTopBorder">
              Market Cap
            </th>

            <th className="headTopBorder headRightBorder">Supply</th>
          </tr>
        </div>

        <TableHead />

        <TableBody input={currentCoins} />
      </table>
    );
	}    
}
