import React              from 'react';
import { Component }      from 'react';
import Table              from './components/Table';
import    Pagination      from './components/Pagination';
import { helperPaginate } from './helpers/helperPaginate'

import InfiniteScroll from "react-infinite-scroll-component";


export default class App extends Component{

	state = {
		coins     :  [],
		count     :  25,
		starts    :  0,
		hasMore   :  true
	}


	render() {

		let a = document.querySelectorAll(".btn")

		return (
<div className='wrap'>
			<InfiniteScroll
				next       = { this.loadMore }
				loader     = { <h4 className='loading'>Loading...</h4> }
				hasMore    = { this.state.hasMore }
				dataLength = { this.state.coins.length }
			>



<div>
					<ul >

						<li id='logo_title' className='headTopBorder headLeftBorder'></li>

						<li id='symbol_title' className='centerText headTopBorder'>Symbol</li>

						<li id='name_title' className='centerText headTopBorder'>Name</li>

						<li id='invisible_title' className='headTopBorder'>
						Symbol<br/>
						Name
						</li>

						<li  className='headTopBorder centerText'>Price USD</li>

						<li  className='headTopBorder'>Change 24h</li>

						<li id='chart_title' className='headTopBorder centerText'>24h Chart</li>

						<li id='invisible_two_title' className='headTopBorder'>
						Volume 24h<br/>
						Market Cap
						</li>

						<li id='volume_24h_title' className='headTopBorder'>Volume 24h</li>

						<li id='market_cap_title' className='headTopBorder'>Market Cap</li>

						<li  className='headTopBorder headRightBorder'>Supply</li>

					</ul>
				</div>

				{/* <div className=''> TOP <strong>99</strong> CRYPTO-CURRENCIES </div> */}

				<div className='table'>

					< Table  input = { this.state.coins }/>

				</div>

					<span>------------</span>
						{/* <span className='btn' onMouseEnter={this.loadMore} >Button</span> */}
					{/* <span>------------</span> */}
						<span>{this.state.starts}</span>


			</InfiniteScroll>
			</div>
		)
	}

	componentDidMount() {

		this.loadCoins();

		setInterval(async () => {

			 this.updateCoins();

		}, 30000);
	}


	loadMore = () => {
		const { starts, count } = this.state;

		let hasMoreHelper = true;
		if ( starts >= 75 ) hasMoreHelper = false;

		this.setState( ({
			starts  : starts + count,
			hasMore : hasMoreHelper
		}))
		this.loadCoins();
	}


	async  loadCoins() {

			const { coins, starts, count } = this.state;
			const url = `/info?starts=${starts}&count=${count}`;
		try {

			const response = await fetch(url, { cache: "no-store" }); 
			const dataJSON = await response.json();

			console.log(response.status);

			this.setState({
				coins : coins.concat(dataJSON)
			});

		} catch (error) {
			console.log(error)
		}
	}


	async  updateCoins() {

			const url = "/instruments";
		try {

			const response = await fetch(url, { cache: "no-store" });
			const dataJSON = await response.json();

			console.log(response.status);

			this.setState({
				coins: dataJSON
			});

		} catch (error) {

			console.log(error)
		}
	}
}
