import React              from 'react';
import { Component }      from 'react';
import Table              from './components/Table';
import Pagination         from './components/Pagination';
import { helperPaginate } from './helpers/helperPaginate'

const axios = require('axios').default;


export default class App extends Component{

	state = {
		coins        : [],
		loading      : true,
		// currentPage  : 1,
		// coinsPerPage : 1,
		starts       : 0,
		count        : 14
	}


	render() {

		if (this.state.loading || !this.state.coins) {

			return <div className='loading'></div>

		} else {

			// const  { currentCoins, pageNumbers } =  helperPaginate(
			// 									this.state.coins,
			// 									this.state.currentPage,
			// 									this.state.coinsPerPage
			// 								);
			// const paginate = ( pageNumber ) => 

			// 	this.setState({
			// 		currentPage: pageNumber
			// })

			return (


					<div className='wrap'>

						<div className=''> TOP <strong>99</strong> CRYPTO-CURRENCIES </div>

						<div className='table'>
						<div  
					onScroll={this.handScroll} 
					ref={(scroller) => {
						this.scroller = scroller
					}}
				          >
							< Table  input = { this.state.coins }/>
</div>
						</div>

						{/* <span className='paginationWrap'>

							< Pagination  pageNumbers = { pageNumbers }  
									paginate    = { paginate } 
									currentPage = { this.state.currentPage }/>
						</span> */}

					</div>

			)
          }    
	}



	// componentDidMount() {
	// 	const { count, starts } = this.state;
	// 	axios
	// 		.get(`/info?starts=${1}&count=${2}`)
	// 		.then(res => this.setState({ coins: res.data }));
	// }

	// fetchImages = () => {
	// 	const { count, starts } = this.state;
	// 	this.setState({ start: this.state.starts + count });
	// 	axios
	// 		.get(`/api/photos?count=${count}&start=${starts}`)
	// 		.then(res =>
	// 			this.setState({ coins: this.state.coins.concat(res.data) })
	// 		);
	// };


     componentDidMount() {
		this.loadCoins() ;



		// this.updateCoins();

		setInterval(async () => {

			 this.updateCoins();

		}, 30000);
	}



	async  loadCoins() {
		for (let i = 0; i < Math.ceil(99 / this.state.count); i++) {
			// const url = "/instruments";
			const { coins, starts, count } = this.state;
			const url = `/info?starts=${starts}&count=${count}`;


			try {

				const response = await fetch(url, { cache: "no-store" }); // fetch(url)
				console.log(response.status);
				const dataJSON = await response.json();

				this.setState({
					coins  : coins.concat(dataJSON),
					starts : starts  + 1,
					loading: false

				});

			} catch (error) {

				console.log(error)
			}

		}
	}




	async  updateCoins() {
		const url = "/instruments";

		try {

			const response = await fetch(url, { cache: "no-store" }); // fetch(url)
			console.log(response.status);
			const dataJSON = await response.json();

			this.setState({
				coins: dataJSON,
				loading: false
			});

			// this.setState(prevState => ({
			// 	coins: dataJSON,
			// 	loading: false
			// }));

		} catch (error) {

			console.log(error)
		}
	}

}
