import React              from 'react';
import { Component }      from 'react';
import Table              from './components/Table';
import Pagination         from './components/Pagination';
import { helperPaginate } from './helpers/helperPaginate'



export default class App extends Component{

	state = {
		coins        : [],
		loading      : true,
		// currentPage  : 1,
		// coinsPerPage : 1,
		starts       : 0,
		count        : 10
	}


	render() {

		if (this.state.loading || !this.state.coins) {

			return <div className='loading'></div>

		} else {

			return (


					<div className='wrap'>

						<div className=''> TOP <strong>99</strong> CRYPTO-CURRENCIES </div>

						<div className='table'>

							< Table  input = { this.state.coins }/>

						</div>

						<div style={pad}>
							<button href="#" onClick={this.loadMore} >Button</button>
							<span>{this.state.starts}</span>
						</div>
					</div>

			)
          }    
	}

	loadMore = () => {
		const { coins, starts, count } = this.state;

		if (starts > 120) return;

		this.setState( ({
			starts : starts + count ,
		}))
		console.log(starts)

		this.loadCoins();
	}


     componentDidMount() {

		this.loadCoins();

		setInterval(async () => {

			 this.updateCoins();

		}, 300000);
	}



	async  loadCoins() {
			// const url = "/instruments";
			const { coins, starts, count } = this.state;
			const url = `/info?starts=${starts}&count=${count}`;


			try {

				const response = await fetch(url, { cache: "no-store" }); // fetch(url)
				console.log(response.status);
				const dataJSON = await response.json();

				this.setState({
					coins  : coins.concat(dataJSON),
					starts : starts  + count ,
					loading: false

				});

			} catch (error) {

				console.log(error)
			}

	}




	async  updateCoins() {
		const url = "/instruments";

		try {

			const response = await fetch(url, { cache: "no-store" }); // fetch(url)
			console.log(response.status);
			const dataJSON = await response.json();

			this.setState({
				coins: dataJSON,
				loading: false
			});

			// this.setState(prevState => ({
			// 	coins: dataJSON,
			// 	loading: false
			// }));

		} catch (error) {

			console.log(error)
		}
	}

}
