import React          from 'react';
import { Component }  from 'react';
import { TableHead }  from './TableHead';
import TableBody      from './TableBody';

export default class Table extends Component{

	constructor(props){
		 super(props);

		 this.state = {
			  currentCoins    : props.input,
			  tableVisibility : props.tableVisibility
		 }
	 }

 static getDerivedStateFromProps( nextProps, prevState ) {

	 if (nextProps.tableVisibility !== prevState.tableVisibility) {

		 return { tableVisibility: nextProps.tableVisibility };
	 }
	 if (nextProps.input !== prevState.input) {

		 return { currentCoins: nextProps.input };
	 }
	 else return null;
 }

	render(){

          const { currentCoins, tableVisibility } = this.state;

		return (
			<div className='menu'>
				<ul className='title_table gradVertical textShadowBlue '>

					<li id='logo_title' className='headTopBorder headLeftBorder '></li>

					<li id='symbol_title ' className='centerText headTopBorder percent-9'>Symbol</li>

					<li id='name_title' className='centerText headTopBorder percent-13'>Name</li>

					<li id='invisible_title' className='headTopBorder '>
					Symbol <br/>
					Name   </li>

					<li  className='headTopBorder centerText percent-13'>Price USD</li>

					<li  className='headTopBorder centerText percent-9'>Change 24h</li>

					<li id='chart_title' className='headTopBorder centerText chartHead percent-13'>Seven Days</li>

					<li id='invisible_two_title' className='headTopBorder'>
					Volume 24h <br/>
					Market Cap </li>

					<li id='volume_24h_title' className='headTopBorder centerText percent-13'>Volume 24h</li>

					<li id='market_cap_title' className='headTopBorder centerText percent-13'>Market Cap</li>

					<li  id='supplyTitle' className='headTopBorder headRightBorder percent-13'>Supply</li>
				</ul>
				<table>

					<TableHead />

					<TableBody input = { currentCoins } tableVisibility = { tableVisibility }/>

				</table>
			</div>
		)
	}    
}
