import React              from 'react';
import { Component }      from 'react';
import Table              from './components/Table';
import    Pagination      from './components/Pagination';
import { helperPaginate } from './helpers/helperPaginate'

import InfiniteScroll from "react-infinite-scroll-component";


export default class App extends Component{

	state = {
		coins     :  [],
		chart     :  [],
		count     :  25,
		starts    :  0,
		hasMore   :  true
	}


	render() {

		let a = document.querySelectorAll(".btn")

		return (
			<div className='wrap'>
				<InfiniteScroll
					next       = { this.loadMore }
					loader     = { <h4 className='loading'>Loading...</h4> }
					hasMore    = { this.state.hasMore }
					dataLength = { this.state.coins.length }
				>


					<div className='table'>

						< Table  input = { this.state.coins }/>

					</div>

				</InfiniteScroll>
							{/* <span>------------</span> */}
							{/* <span className='btn' onMouseEnter={this.loadMore} >Button</span> */}
						     {/* <span>------------</span> */}
							<div style={{ color: "red", fontSize: 20 }} className="loading">
							<span className='border'>{this.state.starts}</span>
							</div>
							<div></div>
		</div>
		)
	}

	componentDidMount() {

		this.loadCoins();

		this.updateCoins();

		setInterval(async () => {

			 this.updateCoins();

		}, 30000);
	}


	loadMore = () => {
		const { starts, count } = this.state;

		let hasMoreHelper = true;
		if ( starts >= 75 ) hasMoreHelper = false;

		this.setState( ({
			starts  : starts + count,
			hasMore : hasMoreHelper
		}))
		this.loadCoins();
	}

	// async loadChart() {
	// 	const { coins, starts, count } = this.state;
	// 	const url = `/chart?starts=${starts}&count=${count}`;
	// try {

	// 	const response = await fetch(url, { cache: "no-store" }); 
	// 	const dataJSON = await response.json();

	// 	console.log(response.status);

	// 	this.setState({
	// 		coins : coins.concat(dataJSON)
	// 	});

	// 	console.log('1hello')
	// 	// setInterval(() => {

	// 	// }, 1000);

	// 	this.loadChart();

	// } catch (error) {
	// 	console.log(error)
	// }


	// }


	async  loadCoins() {

			const { coins, starts, count } = this.state;
			const url = `/info?starts=${starts}&count=${count}`;
		try {

			const response = await fetch(url, { cache: "no-store" }); 
			const dataJSON = await response.json();

			console.log(response.status);

			this.setState({
				coins : coins.concat(dataJSON)
			});

			// this.loadChart();

		} catch (error) {
			console.log(error)
		}
	}


	async  updateCoins() {

			const url = "/instruments";
		try {

			const response = await fetch(url, { cache: "no-store" });
			const dataJSON = await response.json();

			console.log(response.status);

			this.setState({
				coins: dataJSON
			});

		} catch (error) {

			console.log(error)
		}
	}
}
