import React              from 'react';
import { Component }      from 'react';
import Table              from './components/Table';
import    Pagination      from './components/Pagination';
import { helperPaginate } from './helpers/helperPaginate'

import InfiniteScroll from "react-infinite-scroll-component";


export default class App extends Component{

	state = {
		coins     :  [],
		chart     :  [],
		count     :  25,
		starts    :  0,
		hasMore   :  true,
		isReadyLoad
	}


	render() {

		let a = document.querySelectorAll(".btn")

		return (
			<div className='wrap'>

				<InfiniteScroll
					next       =  { this.loadMore }
					hasMore    =  { this.state.hasMore }
					dataLength =  { this.state.coins.length }
					loader     =  { <div>
									<h4 className='loading'>Loading...</h4>
									<div className='border'>{this.state.starts}</div>
								</div> }
				>
					<div className='table'>
						< Table  input = { this.state.coins }/>
					</div>

				</InfiniteScroll>
			</div>
		)
	}

	componentDidMount() {

		this.loadCoins();
		this.loadChart();


		setInterval(async () => {

			 this.updateCoins();

		}, 30000);
	}


	loadMore = () => {
		const { starts, count } = this.state;

		let hasMoreHelper = true;
		if ( starts >= 75 ) hasMoreHelper = false;

		this.setState( ({
			starts  : starts + count,
			hasMore : hasMoreHelper
		}))
		this.loadCoins();
		this.loadChart();
	}



	async  loadCoins() {
		console.log('loadCoins');

			const { coins, starts, count } = this.state;
			const url = `/info?starts=${starts}&count=${count}`;
		try {

			const response = await fetch(url, { cache: "no-store" }); 
			const dataJSON = await response.json();

			console.log(response.status);

			this.setState({
				coins : coins.concat(dataJSON)
			});



		} catch (error) {
			console.log(error)
		}
	}

	async loadChart() {

		console.log('loadChart');

			const { coins, starts, count } = this.state;
			const url = `/chart?starts=${starts}&count=${count}`;
		try {

			const response = await fetch(url, { cache: "no-store" }); 
			const dataJSON = await response.json();

			console.log(response.status);

			this.setState({
				coins : coins.concat(dataJSON)
			});



		} catch (error) {
			console.log(error)
		}
	}	


	async  updateCoins() {

			const url = "/instruments";
		try {

			const response = await fetch(url, { cache: "no-store" });
			const dataJSON = await response.json();

			console.log(response.status);

			this.setState({
				coins: dataJSON
			});

		} catch (error) {

			console.log(error)
		}
	}
}
