            <th id='symbol_title' className='centerText headTopBorder'>Symbol</th>

            <th id='name_title' className='centerText headTopBorder'>Name</th>

            <th id='invisible_title' className='headTopBorder'>
                Symbol<br/>
                Name
            </th>

            <th  className='headTopBorder centerText'>Price USD</th>

            <th  className='headTopBorder'>Change 24h</th>

            <th id='chart_title' className='headTopBorder centerText'>24h Chart</th>

            <th id='invisible_two_title' className='headTopBorder'>
                Volume 24h<br/>
                Market Cap
            </th>

            <th id='volume_24h_title' className='headTopBorder'>Volume 24h</th>

            <th id='market_cap_title' className='headTopBorder'>Market Cap</th>

            <th  className='headTopBorder headRightBorder'>Supply</th>

        </tr>
    </thead>  

    </table> */}
				{/* <div className=''> TOP <strong>99</strong> CRYPTO-CURRENCIES </div> */}

				<div className='table'>

					< Table  input = { this.state.coins }/>

				</div>




			</InfiniteScroll>
						{/* <span>------------</span> */}
						{/* <span className='btn' onMouseEnter={this.loadMore} >Button</span> */}
					{/* <span>------------</span> */}
						<div style={{color: "red", }} className="loading">{this.state.starts}</div>
			</div>
		)
	}

	componentDidMount() {

		this.loadCoins();

		setInterval(async () => {

			 this.updateCoins();

		}, 30000);
	}


	loadMore = () => {
		const { starts, count } = this.state;

		let hasMoreHelper = true;
		if ( starts >= 75 ) hasMoreHelper = false;

		this.setState( ({
			starts  : starts + count,
			hasMore : hasMoreHelper
		}))
		this.loadCoins();
	}


	async  loadCoins() {

			const { coins, starts, count } = this.state;
			const url = `/info?starts=${starts}&count=${count}`;
		try {

			const response = await fetch(url, { cache: "no-store" }); 
			const dataJSON = await response.json();

			console.log(response.status);

			this.setState({
				coins : coins.concat(dataJSON)
			});

		} catch (error) {
			console.log(error)
		}
	}


	async  updateCoins() {

			const url = "/instruments";
		try {

			const response = await fetch(url, { cache: "no-store" });
			const dataJSON = await response.json();

			console.log(response.status);

			this.setState({
				coins: dataJSON
			});

		} catch (error) {

			console.log(error)
		}
	}
}
