import React              from 'react';
import { Component }      from 'react';
import Table              from './components/Table';
import Pagination         from './components/Pagination';
import { helperPaginate } from './helpers/helperPaginate'



export default class App extends Component{

	state = {
		coins   :  [],
		count   :  10,
		starts  :  0
	}
	handleScroll = () => {
		if (this.scroller) {

			console.log(this.scroller);
		}

	}

	render() {

		return (
			<div className='wrap' 
				onScroll={this.handleScroll}
				ref={scroller =>{this.scroller = scroller;}}
				>


				<button href="#" onClick={this.loadMore} >Button</button>


				<div className=''> TOP <strong>99</strong> CRYPTO-CURRENCIES </div>

				<div className='table'>

					< Table  input = { this.state.coins }/>

				</div>

				<div>
					<span>------------</span>
					<button href="#" onClick={this.loadMore} >Button</button>
					<span>------------</span>
					<span>{this.state.starts}</span>
				</div>
			</div>
		)
	}

	componentDidMount() {

		this.loadCoins();

		setInterval(async () => {

			 this.updateCoins();

		}, 300000);
	}

	loadMore = () => {
		const { starts, count } = this.state;

		if (starts > 99) return;
		this.setState( ({
			starts  : starts + count
		}))
		console.log(starts)

		this.loadCoins();
	}

	async  loadCoins() {

		const { coins, starts, count } = this.state;
		const url = `/info?starts=${starts}&count=${count}`;

		try {

			const response = await fetch(url, { cache: "no-store" }); // fetch(url)
			console.log(response.status);
			const dataJSON = await response.json();

			this.setState({
				coins  : coins.concat(dataJSON),
				starts : starts  + count ,

			});

		} catch (error) {

			console.log(error)
		}
	}

	async  updateCoins() {
		const url = "/instruments";

		try {

			const response = await fetch(url, { cache: "no-store" }); // fetch(url)
			console.log(response.status);
			const dataJSON = await response.json();

			this.setState({
				coins: dataJSON
			});

		} catch (error) {

			console.log(error)
		}
	}
}
