import React              from 'react';
import { Component }      from 'react';
import Table              from './components/Table';
import Pagination         from './components/Pagination';
import { helperPaginate } from './helpers/helperPaginate'

import InfiniteScroll from "react-infinite-scroll-component";


export default class App extends Component{

	state = {
		coins     :  [],
		count     :  34,
		starts    :  0,
		hasMore   :  true
	}


	render() {

		return (


			<InfiniteScroll
				next       = { this.loadMore }
				loader     = { <h4 className='loading'>Loading...</h4> }
				hasMore    = { this.state.hasMore }
				dataLength = { this.state.coins.length }
			>

			<div className='wrap'>


				<div className=''> TOP <strong>99</strong> CRYPTO-CURRENCIES </div>

				<div className='table'>

					< Table  input = { this.state.coins }/>

				</div>

				{/* <span >------------</span><span>{this.state.starts}</span> */}
			</div>

			</InfiniteScroll>
		)
	}

	componentDidMount() {

		this.loadCoins();

		setInterval(async () => {

			 this.updateCoins();

		}, 300000);
	}

	loadMore = () => {
		const { starts, count } = this.state;

		// if (starts > 90) return;
		this.setState( ({
			starts  : starts + count
		}))
		console.log(starts)

		this.loadCoins();
	}

	async  loadCoins() {

		const { coins, starts, count } = this.state;
		const url = `/info?starts=${starts}&count=${count}`;

		try {

			const response = await fetch(url, { cache: "no-store" }); // fetch(url)
			console.log(response.status);
			const dataJSON = await response.json();

			let hasMoreHelper = true;
			if (starts >= 68)  hasMoreHelper = false;


			this.setState({
				coins   : coins.concat(dataJSON),
				starts  : starts  + count ,
				hasMore : hasMoreHelper
			});

		} catch (error) {

			console.log(error)
		}
	}

	async  updateCoins() {
		const url = "/instruments";

		try {

			const response = await fetch(url, { cache: "no-store" }); // fetch(url)
			console.log(response.status);
			const dataJSON = await response.json();

			this.setState({
				coins: dataJSON
			});

		} catch (error) {

			console.log(error)
		}
	}
}
