	// 		const dataJSON = await response.json();

	// 		if ( counter === 0 ) {
	// 			this.setState({
	// 				coins   : dataJSON,
	// 				counter : counter + 1
	// 			});
	// 		} else {
	// 			this.setState({
	// 				coins   : coins.concat(dataJSON)
	// 			});
	// 		}


	// 	} catch (error) {
	// 		console.log(error)
	// 	}
	// }	


	async  updateCoins() {
// console.log("change")
			const url = "/instruments";
		try {

			const response = await fetch(url, { cache: "no-store" });
			const dataJSON = await response.json();

			// console.log(response.status);

			this.setState({
				coins: dataJSON
			});
		} catch (error) {

			console.log(error)
		}
	}
}
